Map:
&fontset-settings; &layers; &layer-shapefiles;
[natural] = 'marsh' or [natural] = 'wetland'
[natural] = 'land'
[leisure] = 'swimming_pool' &maxscale_zoom14;
[leisure] = 'playground' &maxscale_zoom13;
[tourism] = 'camp_site' or [tourism]='caravan_site' or [tourism] = 'picnic_site' &maxscale_zoom13;
[tourism] = 'attraction' &maxscale_zoom10;
[landuse] = 'vineyard'
[landuse] = 'orchard'
&maxscale_zoom10; &minscale_zoom14; [landuse] = 'cemetery' or [landuse] = 'grave_yard' or [amenity]='grave_yard'
[landuse]='field' or [natural]='field'
&maxscale_zoom14; ([landuse] = 'cemetery' or [landuse] = 'grave_yard' or [amenity]='grave_yard') and [religion] = 'INT-generic'
[landuse] = 'meadow' or [landuse] = 'grass'
[leisure] = 'park' or [leisure] = 'recreation_ground'
[tourism] = 'zoo'
[leisure] = 'common'
[leisure] = 'garden'
[leisure] = 'golf_course'
[landuse] = 'forest'
[landuse]='farmyard'
[landuse] = 'recreation_ground' or [landuse] = 'conservation' &maxscale_zoom10;
[landuse] = 'village_green' &maxscale_zoom11;
[natural] = 'wood' or [landuse] = 'wood'
[natural] = 'grassland'
[natural] = 'scrub'
[aeroway] = 'aerodrome' &maxscale_zoom12;
[highway] = 'services' or [highway] = 'rest_area' &maxscale_zoom14;
[leisure] = 'sports_centre' or [leisure] = 'stadium'
[leisure]='track'
[leisure] = 'pitch'
landcover
(select way,aeroway,amenity,landuse,leisure,man_made,military,"natural",power,tourism,name,highway, case when religion in ('christian','jewish') then religion else 'INT-generic'::text end as religion from &prefix;_polygon where landuse is not null or leisure is not null or aeroway in ('apron','aerodrome') or "natural" in ('field','beach','desert','heath','mud','grassland','wood','sand','scrub') or tourism in ('attraction','camp_site','caravan_site','picnic_site','zoo') or highway in ('services','rest_area') order by z_order,way_area desc ) as leisure
&datasource-settings;
sports_grounds
(select way,leisure, case when leisure='pitch' then 2 when leisure='track' then 1 else 0 end as prio from &prefix;_polygon where leisure in ('sports_centre','stadium','pitch','track') order by z_order,prio,way_area desc ) as sports_grounds
&datasource-settings;
water_areas
(select way,"natural",waterway,landuse,name from &prefix;_polygon where (waterway in ('dock','mill_pond','riverbank','canal') or landuse in ('reservoir','water','basin') or "natural" in ('lake','water','land','glacier','bay')) and building is null order by z_order,way_area desc ) as water_areas &datasource-settings;
water-areas-overlay
(select way,"natural" from &prefix;_polygon where "natural" in ('marsh','wetland') and building is null order by z_order,way_area desc ) as water_areas &datasource-settings;
